mapper.insert:
/* cgBoard.insert */
INSERT INTO CASH_GUARD.CG_BOARD (
     BBS_UID
    ,GROUP_UID
    ,TITLE
    ,CONTENT
    ,FILE_UID
    ,THUMB_UID
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
    ,DELETE_AT
) VALUES (
     #{bbsUid}
    ,#{groupUid}
    ,#{title}
    ,#{content}
    ,#{fileUid}
    ,#{thumbUid}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
    ,'N'
)

mapper.update:
/* cgBoard.update */
UPDATE CASH_GUARD.CG_BOARD
   SET TITLE = #{title}
      ,CONTENT = #{content}
      ,FILE_UID = #{fileUid}
      ,THUMB_UID = #{thumbUid}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.delete:
/* cgBoard.delete */
UPDATE CASH_GUARD.CG_BOARD
   SET DELETE_AT = 'Y'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.select:
/* cgBoard.page */ SELECT CBBS.UID AS BBS_UID ,CBBS.BBS_NM AS BBS_NM ,CBBS.BBS_TP_CD AS BBS_TP_CD ,CASE WHEN CBBS.BBS_TP_CD = 'NOTICE' THEN '공지사항' WHEN CBBS.BBS_TP_CD = 'BOARD' THEN '자유 게시판' WHEN CBBS.BBS_TP_CD = 'QNA' THEN '질의 응답' WHEN CBBS.BBS_TP_CD = 'FAQ' THEN 'faq' END AS BBS_TP_CD_NM ,CGROUP.UID AS GROUP_UID ,CGROUP.GROUP_NM AS GROUP_NM ,CBOARD.UID AS BOARD_UID ,CBOARD.TITLE AS TITLE ,CBOARD.CONTENT AS CONTENT ,CBOARD.FILE_UID AS FILE_UID ,CBOARD.THUMB_UID AS THUMB_UID ,CBOARD.DELETE_AT AS DELETE_AT ,CBOARD.REG_UID AS REG_UID ,CBOARD.REG_DT AS REG_DT ,CU_REG.USER_NM AS REG_NM ,CBOARD.UPDT_UID AS UPDT_UID ,CBOARD.UPDT_DT AS UPDT_DT ,CU_UPDT.USER_NM AS UPDR_NM FROM CASH_GUARD.CG_BOARD CBOARD INNER JOIN CASH_GUARD.CG_BBS CBBS ON CBBS.UID = CBOARD.BBS_UID INNER JOIN CASH_GUARD.CG_GROUP CGROUP ON CGROUP.UID = CBOARD.GROUP_UID LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CBOARD.REG_UID LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CBOARD.UPDT_UID WHERE 1 = 1 AND CBOARD.DELETE_AT = 'N' AND CBOARD.BBS_UID = #{bbsUid} AND CBOARD.GROUP_UID = #{groupUid}
AND CBOARD.TITLE LIKE CONCAT('%',#{title},'%')
AND CU_REG.USER_NM LIKE CONCAT('%',#{regNm},'%')
ORDER BY CBOARD.REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* cgBoard.count */ SELECT COUNT(*) FROM CASH_GUARD.CG_BOARD CBOARD INNER JOIN CASH_GUARD.CG_BBS CBBS ON CBBS.UID = CBOARD.BBS_UID INNER JOIN CASH_GUARD.CG_GROUP CGROUP ON CGROUP.UID = CBOARD.GROUP_UID LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CBOARD.REG_UID LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CBOARD.UPDT_UID WHERE 1 = 1 AND CBOARD.DELETE_AT = 'N' AND CBOARD.BBS_UID = #{bbsUid} AND CBOARD.GROUP_UID = #{groupUid}
AND CBOARD.TITLE LIKE CONCAT('%',#{title},'%')
AND CU_REG.USER_NM LIKE CONCAT('%',#{regNm},'%')

/* cgBoard.list */ SELECT CBBS.UID AS BBS_UID ,CBBS.BBS_NM AS BBS_NM ,CBBS.BBS_TP_CD AS BBS_TP_CD ,CASE WHEN CBBS.BBS_TP_CD = 'NOTICE' THEN '공지사항' WHEN CBBS.BBS_TP_CD = 'BOARD' THEN '자유 게시판' WHEN CBBS.BBS_TP_CD = 'QNA' THEN '질의 응답' WHEN CBBS.BBS_TP_CD = 'FAQ' THEN 'faq' END AS BBS_TP_CD_NM ,CGROUP.UID AS GROUP_UID ,CGROUP.GROUP_NM AS GROUP_NM ,CBOARD.UID AS BOARD_UID ,CBOARD.TITLE AS TITLE ,CBOARD.CONTENT AS CONTENT ,CBOARD.FILE_UID AS FILE_UID ,CBOARD.THUMB_UID AS THUMB_UID ,CBOARD.DELETE_AT AS DELETE_AT ,CBOARD.REG_UID AS REG_UID ,CBOARD.REG_DT AS REG_DT ,CU_REG.USER_NM AS REG_NM ,CBOARD.UPDT_UID AS UPDT_UID ,CBOARD.UPDT_DT AS UPDT_DT ,CU_UPDT.USER_NM AS UPDR_NM FROM CASH_GUARD.CG_BOARD CBOARD INNER JOIN CASH_GUARD.CG_BBS CBBS ON CBBS.UID = CBOARD.BBS_UID INNER JOIN CASH_GUARD.CG_GROUP CGROUP ON CGROUP.UID = CBOARD.GROUP_UID LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CBOARD.REG_UID LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CBOARD.UPDT_UID WHERE 1 = 1 AND CBOARD.DELETE_AT = 'N' AND CBOARD.BBS_UID = #{bbsUid} AND CBOARD.GROUP_UID = #{groupUid}
AND CBOARD.TITLE LIKE CONCAT('%',#{title},'%')
AND CU_REG.USER_NM LIKE CONCAT('%',#{regNm},'%')
ORDER BY CBOARD.REG_DT DESC
