mapper.insert:
/* cgGroup.insert */
INSERT INTO CASH_GUARD.CG_GROUP (
     USER_UID
    ,GROUP_NM
    ,IS_PRIVATE
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{userUid}
    ,#{groupNm}
    ,#{isPrivate}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
)

mapper.update:
/* cgGroup.update */
UPDATE CASH_GUARD.CG_GROUP
   SET GROUP_NM = #{groupNm}
      ,IS_PRIVATE = #{isPrivate}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.select:
/* cgGroup.page */ SELECT CGGROUP.UID ,CGGROUP.USER_UID ,CU_MASTER.USER_NM AS USER_NM ,CGGROUP.GROUP_NM ,CGGROUP.IS_PRIVATE ,( SELECT COUNT(*) FROM CASH_GUARD.CG_GROUP_MEMBER CM WHERE CM.GROUP_UID = CGGROUP.UID AND CM.IS_APPROVED = 'Y' ) AS APPROVED_MEMBER ,( SELECT COUNT(*) FROM CASH_GUARD.CG_GROUP_MEMBER CM2 WHERE CM2.GROUP_UID = CGGROUP.UID ) AS TOTAL_MEMBER ,CGGROUP.REG_UID ,CGGROUP.REG_DT AS REG_DT ,CU_REG.USER_NM AS REG_NM ,CGGROUP.UPDT_UID ,CGGROUP.UPDT_DT AS UPDT_DT ,CU_UPDT.USER_NM AS UPDT_NM FROM CASH_GUARD.CG_GROUP CGGROUP INNER JOIN CASH_GUARD.CG_USER CU_MASTER ON CU_MASTER.UID = CGGROUP.USER_UID LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CGGROUP.REG_UID LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CGGROUP.UPDT_UID WHERE 1 = 1
AND CGGROUP.IS_PRIVATE = #{isPrivate}
AND CGGROUP.GROUP_NM = #{groupNm}
ORDER BY CGGROUP.REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

/* cgGroup.count */
SELECT COUNT(*)
  FROM CASH_GUARD.CG_GROUP CGGROUP
 INNER JOIN CASH_GUARD.CG_USER CU_MASTER ON CU_MASTER.UID = CGGROUP.USER_UID
  LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CGGROUP.REG_UID
  LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CGGROUP.UPDT_UID
 WHERE 1 = 1

   AND CGGROUP.IS_PRIVATE = #{isPrivate}


   AND CGGROUP.GROUP_NM = #{groupNm}

/* cgGroup.page */ SELECT CGGROUP.UID ,CGGROUP.USER_UID ,CU_MASTER.USER_NM AS USER_NM ,CGGROUP.GROUP_NM ,CGGROUP.IS_PRIVATE ,( SELECT COUNT(*) FROM CASH_GUARD.CG_GROUP_MEMBER CM WHERE CM.GROUP_UID = CGGROUP.UID AND CM.IS_APPROVED = 'Y' ) AS APPROVED_MEMBER ,( SELECT COUNT(*) FROM CASH_GUARD.CG_GROUP_MEMBER CM2 WHERE CM2.GROUP_UID = CGGROUP.UID ) AS TOTAL_MEMBER ,CGGROUP.REG_UID ,CGGROUP.REG_DT AS REG_DT ,CU_REG.USER_NM AS REG_NM ,CGGROUP.UPDT_UID ,CGGROUP.UPDT_DT AS UPDT_DT ,CU_UPDT.USER_NM AS UPDT_NM FROM CASH_GUARD.CG_GROUP CGGROUP INNER JOIN CASH_GUARD.CG_USER CU_MASTER ON CU_MASTER.UID = CGGROUP.USER_UID LEFT JOIN CASH_GUARD.CG_USER CU_REG ON CU_REG.UID = CGGROUP.REG_UID LEFT JOIN CASH_GUARD.CG_USER CU_UPDT ON CU_UPDT.UID = CGGROUP.UPDT_UID WHERE 1 = 1
AND CGGROUP.IS_PRIVATE = #{isPrivate}
AND CGGROUP.GROUP_NM = #{groupNm}
ORDER BY CGGROUP.REG_DT DESC

/* cgGroup.checkIfExist */
SELECT COUNT(*)
  FROM CASH_GUARD.CG_GROUP_MEMBER
 WHERE USER_UID = #{userUid}
   AND GROUP_UID = #{groupUid}
