mapper.update:
/* cgUser.updateLastLoginDtAndPwdFailCnt */
UPDATE CASH_GUARD.CG_USER
   SET LAST_CONN_DT = NOW()
      ,PWD_FAIL_CNT = 0
 WHERE USER_ID = #{userId}

/* cgUser.updatePwdFailCnt */
UPDATE CASH_GUARD.CG_USER
   SET PWD_FAIL_CNT = #{pwdFailCnt}

      ,IS_LOCKED = 'Y'

 WHERE USER_ID = #{userId}

mapper.insert:
/* cgUser.insert */
INSERT INTO CASH_GUARD.CG_USER (
     USER_ID
    ,PASSWORD
    ,USER_NM
    ,USER_TEL
    ,USER_EMAIL
    ,LAST_CONN_DT
    ,LAST_PASSWD_CHNG_DT
    ,IS_ENABLE
    ,PWD_FAIL_CNT
    ,IS_LOCKED
    ,ROLE
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
) VALUES (
     #{userId}
    ,#{password}
    ,#{userNm}
    ,#{userTel}
    ,#{userEmail}
    ,NOW()
    ,(NOW() + INTERVAL '90' DAY)
    ,'Y'
    ,0
    ,'N'
    ,#{role}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
)

mapper.select:
/* cgUser.page */ SELECT UID ,USER_ID ,PASSWORD ,USER_NM ,USER_TEL ,USER_EMAIL ,LAST_CONN_DT ,LAST_PASSWD_CHNG_DT ,PROFILE_UID ,IS_ENABLE ,PWD_FAIL_CNT ,IS_LOCKED ,ROLE FROM CASH_GUARD.CG_USER WHERE 1=1
AND USER_NM LIKE CONCAT('%',#{userNm},'%')
ORDER BY REG_DT DESC
LIMIT #{endNumber} OFFSET #{startNumber}

mapper.update:
/* cgUser.update */
UPDATE CASH_GUARD.CG_USER
   SET USER_NM = #{userNm}
      ,USER_TEL = #{userTel}
      ,USER_EMAIL = #{userEmail}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()

      ,PASSWORD = #{password}

 WHERE UID = #{uid}

/* cgUser.updateLastPasswdChngDt */
UPDATE CASH_GUARD.CG_USER
   SET LAST_PASSWD_CHNG_DT = (NOW() + INTERVAL '90' DAY)
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()

      ,PASSWORD = #{password}

 WHERE USER_ID = #{userId}

/* cgUser.updateUserToUnLock */
UPDATE CASH_GUARD.CG_USER
   SET IS_LOCKED = 'N'
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

/* cgUser.updateUserIsEnable */
UPDATE CASH_GUARD.CG_USER
   SET IS_ENABLE = #{isEnable}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE UID = #{uid}

mapper.select:
/* cgUser.checkIfAuthUser */
SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
  FROM CASH_GUARD.CG_USER
 WHERE USER_EMAIL = #{userEmail}
