mapper.select:
/* customJdbc.getIndex */
SELECT COALESCE(MAX(MESSAGE_INDEX), -1) + 1
  FROM CASH_GUARD.CG_CHAT_MEMORY
 WHERE SESSION_ID = #{sessionId}

mapper.insert:
/* customJdbc.insertConversation */
INSERT INTO CASH_GUARD.CG_CHAT_MEMORY (
     SESSION_ID
    ,MESSAGE_INDEX
    ,ROLE
    ,CONTENT
    ,USER_ID
    ,DELETE_AT
) VALUES (
     #{sessionId}
    ,#{messageIndex}
    ,#{role}
    ,#{content}
    ,#{userId}
    ,'N'
)

mapper.select:
/* customJdbc.getChatList */
SELECT ROLE
      ,CONTENT
  FROM CASH_GUARD.CG_CHAT_MEMORY
 WHERE SESSION_ID = #{sessionId}
   AND DELETE_AT = 'N'
 ORDER BY MESSAGE_INDEX DESC
 LIMIT #{limit}

mapper.update:
/* customJdbc.clearBySessionId */
UPDATE CASH_GUARD.CG_CHAT_MEMORY
   SET DELETE_AT = 'Y'
 WHERE SESSION_ID = #{sessionId}

/* customJdbc.clearByUserId */
UPDATE CASH_GUARD.CG_CHAT_MEMORY
   SET DELETE_AT = 'Y'
 WHERE USER_id = #{userId}
